const navSlide=()=>{const e=document.querySelector(".burger"),n=document.querySelector(".nav__links"),t=document.querySelectorAll(".nav__links li");e.addEventListener("click",()=>{n.classList.toggle("nav-active"),t.forEach((e,n)=>{e.getElementsByClassName.animation?e.style.animation="":e.style.animation=`navLinkFade 0.5s ease forwards ${n/7+.5}s`}),e.classList.toggle("toggle")})};navSlide();